sys_variable_value.value:
(function execute(inputs, outputs) {
    // ... code ...
    var taskGr = inputs.task;
    var worknotes = taskGr.short_description + ' ' + taskGr.vulnerability.summary + ' ';
    var grNotes = new GlideRecord('sys_journal_field');
    //element_id=0bd6f41d4f416280e5fa9a701310c7a7^element=work_notes^valueNOT LIKESystem Generated
    var enQuery = 'element=work_notes^valueNOT LIKESystem Generated^element_id=' + taskGr.sys_id;
    grNotes.addEncodedQuery(enQuery);
    grNotes.orderBy('sys_created_on');
    grNotes.query();
    while (grNotes.next()) {
        var notesValue = grNotes.value;
        notesValue = notesValue.replaceAll('"', '');
        if (notesValue.indexOf("[code]") > -1) {
            var notesArray = notesValue.split("[code]");
			worknotes += notesArray[0] + ' ';
            var recordNumber = notesArray[1];
            var hrefArray = recordNumber.split("[/code] ");
            var hrefHtml = hrefArray[0];
            var recNumber = getRecordNumber(hrefHtml);
			if(JSUtil.notNil(recNumber)) {
				worknotes += recNumber ;
			}
            
       } else {
            worknotes += notesValue;
        }
		worknotes += '. ';
    }

    outputs.worknotes = parseData(worknotes);


    function parseData(inputString) {
        var searchRegExp = /\s/g;
        var replaceWith = ' ';
        var sReading = inputString;
        sReading = sReading.replace(searchRegExp, replaceWith);
        searchRegExp = '/,/g';
        sReading = sReading.replace(/,/g, replaceWith);
        sReading = sReading.replaceAll('  ', ' ');
        sReading = sReading.replaceAll('[/code]', ' ');
        sReading = sReading.replaceAll('-', ' ');
        sReading = sReading.replaceAll('. .', '.');

        sReading = sReading.replaceAll('/', '-');
        sReading = sReading.replaceAll('https://dev228428.service-now.com/nav_to.do?uri=%2Fkb_view.do%3Fsysparm_article%3D', '');
        return sReading;
    }

    function getRecordNumber(recordhtml) {
        var htmlString = recordhtml;
        var strippedString = htmlString.replace(/<[^>]*>/g, '');
        return strippedString // Outputs: Example

    }
})(inputs, outputs);

