sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var grToken = new GlideRecord('x_146833_genaico_0_watsonxaitoken');
grToken.get('16b05854970546105f703dafe153af20');
    grToken.u_expires_in = inputs.expiresin;
    grToken.u_access_token = inputs.accesstoken;
    grToken.u_token_type = inputs.tokentype;
    grToken.u_scope = inputs.scope;
    grToken.u_expiration = inputs.expiration;
    grToken.u_refresh_token = inputs.refreshtoken;
    grToken.update();
})(inputs, outputs);

